mapper.select:
SELECT G.GNR_ID , G.GNR_NM , TO_CHAR(G.CRT_DT, 'YYYY-MM-DD') CRT_DT , G.CRTR , TO_CHAR(G.MDFY_DT, 'YYYY-MM-DD') MDFY_DT , G.MDFYR , G.USE_YN , CRT_M.MBR_NM CRT_MBR_NM , MDFY_M.MBR_NM MDFY_MBR_NM FROM GNR G INNER JOIN MBR CRT_M ON G.CRTR = CRT_M.MBR_ID INNER JOIN MBR MDFY_M ON G.MDFYR = MDFY_M.MBR_ID WHERE G.DEL_YN = 'N'
AND G.GNR_NM = {#_parameter}
ORDER BY G.GNR_ID ASC

mapper.update:
	UPDATE GNR
       SET GNR_NM = #{gnrNm}
       	 , MDFY_DT = SYSDATE
       	 , MDFYR = #{mdfyr}
       	 , USE_YN = #{useYn}
	 WHERE GNR_ID = {gnrId}

	UPDATE GNR
       SET DEL_YN = 'Y' 
	 WHERE GNR_ID = #{_parameter}
