mapper.update:
	UPDATE MV
	   SET MV_TTL = #{mvTtl}
	     , ENG_TTL = #{engTtl}
	     , SCRN_STT = #{scrnStt}
	     , SCRN_TM = #{scrnTm}
	     , OPNG_DT = TO_DATE(#{opngDt}, 'YYYY-MM-DD')
	     , GRD = #{grd}
	     , PSTR = #{pstr}
	     , SMR = #{smr}
	     , MDFY_DT = SYSDATE
	     , MDFYR = #{mdfyr}
	     , USE_YN = NVL(#{useYn}, 'N')
     WHERE MV_ID = #{mvId}

UPDATE MV
   SET DEL_YN = 'Y'
 WHERE MV_ID = #{_parameter}
